INSERT INTO t_dp_news
   (`C_ID`, `C_CREATE_USER`, `C_NEWS_CONTENT`, `C_NEWS_CREATE_TIME`, `C_NEWS_ORDER_ID`, `C_NEWS_SOURCE`, `C_NEWS_SOURCE_URL`, `C_NEWS_STSTUS`, `C_NEWS_SUMMARY`, `C_NEWS_TITLE`, `C_UPDATE_TIME`, `C_TYPE_ID`, `C_CLICK_TIME`)
VALUES
   ('107', 'admin', '<p>&nbsp;&nbsp;&nbsp; 去年年初，国务院下发《推进三网融合的总体方案》，首次明确提出了“加快培育市场主体，组建国家级有线电视网络公司，初步形成适度竞争的产业格局”的目标。目前，各省级有线电视网络的整合仍在进行中，先整合省网将使本来就很复杂的公司股权结构变得更为复杂，完成总公司的“接盘”将难上加难，更重要的是各省的整合，诸如硬件、软件甚至是人、财、物等各方面并没有形成统一。以各地有线网络的物理网为例，省、市甚至县乡镇这个层面都没有形成统一的物理网标准。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 对此，中央财政将先期投资100多亿元进行中国广电网络的主体搭建，其中包括总公司基本框架的组建费用和播控平台、网间结算等业务总平台的搭建费用。此后，再制定国发5号文提出的“全国有线电视网络统一规划、统一建设、统一运营、统一管理”的统一标准，完成对各省网的并入。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 目前，各省都在抓紧当地有线网络的整合，有线资产出资人结构不同，若重新统一物理网标准落实到执行层面并非易事，而且各地有线网基本都是自筹资金建设，独立运营，所以如何“接盘”，特别是对上市公司在各地有线网络的资产如何“上收”成为当前最大的整合难点。面对困难，各省也是整合有道。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 四川模式：观念入手 改变行动</p>\r\n<p>&nbsp;&nbsp;&nbsp; 四川有线按照“行政推动、市场运作”的整合推进原则，遵循“存量保值、增量分成”的利益分配原则，“在整合方式上，先省公司挂牌，后资产评估注入，省、市、县全覆盖。”广电网络整合在全国起点高，目前，全国能实现由省到村“一张网”的省份并不多。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 在宜宾广电网络，原来的“收费大厅”变成“营业大厅”，过去的“收费员”转型为“营销员”。“这不仅仅是营业网点和岗位职称的变化，更是改革带来的观念之变。”宜宾分公司经理杨荣富说，“收费大厅”仅是收取费用的场所，而“营业厅”涵盖收费、营销、服务等多项内容，“一个核心就是客户本位”。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 浙江模式：稳定关系 效益统一</p>\r\n<p>&nbsp;&nbsp;&nbsp; 浙江有线网络整合的方式是以华数集团为基础，各级广播电视机构共同参与，推进全省统一的现代化传播平台建设。2011年底前，市级广电有线网络全部加入省网络公司，实现资本联合；2012年6月底前，县级广电有线网络进入省网络公司，实现全省广电有线网络资本联合，基本完成网络整合发展任务。明确推进整合的方式途径，以资本为纽带，资产联合为基础，多种合作方式并存，最终形成全省统一市场和服务的紧密型联合发展格局。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 在整合过程中，妥善处理好省与市、县的关系，改革与发展、稳定的关系，事业与产业的关系，把服务大局、服务群众作为网络整合的出发点和落脚点，坚持社会效益和经济效益相统一。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 甘肃模式：承上启下 统一运营</p>\r\n<p>&nbsp;&nbsp;&nbsp; 首先增强网络整合工作的自觉性和主动性。其次先完成市级分公司挂牌，做好“统一负责人选配、统一财务报表、统一人事管理、统一运营业务”等实质性工作。按照全省网络整合工作的总体部署和领导小组办公室的具体安排。再次，在2011年底之前对全省广电网络资产和运营机构进行重组和改制，组建“统一规划、统一建设、统一管理、统一运营”的省广电网络公司。</p>', '2012-10-20 14:40:55', 0, '慧聪广电网', 'http://info.broadcast.hc360.com/2011/10/090834466079.shtml', '1002', '网络整合：明确目标 严格执行', '网络整合明确目标严格执行', '2012-10-20 15:40:55', '75', 0);
INSERT INTO t_dp_news
   (`C_ID`, `C_CREATE_USER`, `C_NEWS_CONTENT`, `C_NEWS_CREATE_TIME`, `C_NEWS_ORDER_ID`, `C_NEWS_SOURCE`, `C_NEWS_SOURCE_URL`, `C_NEWS_STSTUS`, `C_NEWS_SUMMARY`, `C_NEWS_TITLE`, `C_UPDATE_TIME`, `C_TYPE_ID`, `C_CLICK_TIME`)
VALUES
   ('108', 'admin', '<p>&nbsp;&nbsp;&nbsp; Broadcom向全球市场推出数字电视转换技术&nbsp; 新产品采用单一平台设计，支持世界各地多种电视标准，能上网、能看电影、能玩游戏的电视就是智能电视吗？当然不是。智能电视除了具备传统电视的功能外，还应该具有开放的嵌入式操作系统平台和丰富的应用商店，同时具有网络接入功能。如今，智能电视不断刷新人们对于电视的期待和想象，使用电视购物、娱乐、学习、游戏不再是梦想。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 看系统比应用</p>\r\n<p>&nbsp;&nbsp;&nbsp; 如果问为什么智能电视能够吸引我们，无疑就是内置于电视的海量应用程序。应用商店的出现使传统电视被赋予了新的定义，用户可以自主安装和卸载各类应用程序和软件，从而满足多样化和个性的使用需求，并拥有了无限的可能性。例如三星搭建的全球首个基于高清电视平台的应用程序商店，在全球范围内拥有900个智能电视专属应用程序，为用户带来丰富多彩的智能享受。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 而这些丰富的应用，都是基于一个智能的操作平台。智能电视采用的操作平台有很多，除了开放的Android操作系统、windows系统、企业自主研发的操作系统还有备受期待的苹果IOS系统等。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 目前智能电视采用Android操作系统的比较多，随着操作系统的不断完善，能够为消费者提供更多更好应用的操作系统将成为主流。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 体验多屏互动</p>\r\n<p>&nbsp;&nbsp;&nbsp; 三网融合带动了网络和终端的升级，使基于电视、电脑、手机等终端的融合应用能够在多个屏幕上进行无缝切换，这样的优势在于终端设备上的资源可以共享。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 在多屏互动的时代，用户可以通过智能系统自动搜索并连接无线局域网内的电视，自由传送手机内的图片、音频、全高清视频至电视屏幕，还可以利用多屏互动实现遥控电视、语音输入、触摸游戏等功能。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 简易操作不可少</p>\r\n<p>&nbsp;&nbsp;&nbsp; 智能电视功能日趋复杂化，但操作的便捷性、整体的易用性却不能少。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 智能电视的出现，使遥控器也发生了一些明显的变化，除了通常的遥控器功能还有很多的延伸，遥控器可以有语音功能，能够用语音来控制电视。另外，智能电视也可以通过手势控制进行操作。基于语音控制和手势识别功能的人机交互方式能为消费者带来全新的体验。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 小贴士</p>\r\n<p>&nbsp;&nbsp;&nbsp; 互联网电视不是智能电视</p>\r\n<p>&nbsp;&nbsp;&nbsp; 智能电视和互联网电视都可以连接网络，并浏览网上信息，很多消费者误认为互联网电视和智能电视基本相同。这种观点是不正确的。相对于互联网电视，智能电视最大的特点就是应用商店，用户可以自主安装或卸载软件，通过与应用商店连接满足个性化需求。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 智能电视因需而购</p>\r\n<p>&nbsp;&nbsp;&nbsp; 目前市场上电视种类很多，3D电视、互联网电视、智能电视，还有刚刚兴起的云电视，消费者应该明确需求再做选择。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 可以说，智能时代使用户从“看电视”向“玩电视”转变，未来，电视将不再是一个独立的智能平台，无限扩展、无限想象，拥有更多更好的应用和用户体验的产品，才是消费者购买的首选。</p>', '2012-10-20 14:40:55', 0, '中国电子报', 'http://info.broadcast.hc360.com/2011/10/100915466859.shtml', '1001', '智能电视不断刷新人们对于电视的期待和想象，使用电视购物、娱乐、学习、游戏不再是梦想。', '智能电视不断刷新人们的想象', '2012-10-20 15:40:55', '75', 0);
INSERT INTO t_dp_news
   (`C_ID`, `C_CREATE_USER`, `C_NEWS_CONTENT`, `C_NEWS_CREATE_TIME`, `C_NEWS_ORDER_ID`, `C_NEWS_SOURCE`, `C_NEWS_SOURCE_URL`, `C_NEWS_STSTUS`, `C_NEWS_SUMMARY`, `C_NEWS_TITLE`, `C_UPDATE_TIME`, `C_TYPE_ID`, `C_CLICK_TIME`)
VALUES
   ('109', 'admin', '<p>&nbsp;&nbsp;&nbsp; 自智能电视入市以来，互联网电视的市场份额开始急剧下滑，很多朋友甚至认为，互联网电视只是智能电视的过渡产品，终究会被智能电视所取代。也有专家认为中国市场同国外不同，在中国推广智能家电，势必牵涉到工信部与广电总局，制度不成熟导致两大部门利益分配不均，再加上中国政府对于互联网内容的严格把关与审核，但智能家电会同前几年热炒的互联网电视一样，有发展但不会成为主流。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 据中国电子商会消费电子产品调查办公室近日发布的《智能电视消费市场需求分析》显示，尽管目前智能电视销量只占平板电视市场的10%，但智能电视预期购买率已达到32%，消费者对智能电视期望很高，其中像电脑一样随意上网、浏览网页和视频，以及多屏互动功能，成为智能电视热门功能需求。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 智能电视发展是大势所趋</p>\r\n<p>&nbsp;&nbsp;&nbsp; 智能电视之所以需求是因为智能电视具备以下特征：</p>\r\n<p>&nbsp;&nbsp;&nbsp; 第一，智能电视可以快速、便捷地完成各种操作。比如LG的动感遥控器更像一个“鼠标”。只需要手持并指向屏幕，然后把光标移动到想要的选项上，再点击一下就可以激活选项。更为智能的是人体体感操控。海信的智能感应触控电视Hi-Touch以及海尔的人机互动智能电视，可实现一定距离内的遥感控制，完全不用遥控器便可用手掌等部位的上下左右移动、点击等实现对电视的自由隔空操作。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 第二，智能电视要具备下载、运行各种软件的能力。这些应用软件并不是通用的版本，而应该是为电视机专门开发的版本。厂商认为将来电视用户可以通过下载各种应用软件满足自己对生活、娱乐甚至工作的需求，同时也会成为其利润的重要来源.为了满足智能电视的这个功能，智能电视需要有一定运算能力的CPU，缓存、内部存储装置等等模块，从这个意义上说，智能电视则成了一台内置了电脑的电视。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 对于智能电视市场迅速崛起，GuidelineResearch格兰研究认为，主要包括以下几点：</p>\r\n<p>&nbsp;&nbsp;&nbsp; 首先，国家“三网融合”，将改变有线数字电视的单一服务模式，内容格式的多样性、服务种类的多样性、接入方式的多样性将成为三网融合环境下的数字电视新特点。基于开放软件平台下的智能电视成为数字家庭的核心是必然趋势。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 其次，电视整机正从平板时代向互联网时代甚至向目前的智能时代跨越，电视机巨头为避免在数字家庭中的核心地位被智能机顶盒取代，必然会向技术平台等内容产业链扩张。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 再次，手机智能操作系统已日渐惨烈，应用平台系统想要获得更大的份额就必须寻找新的蓝海，如此情形下，智能电视成了下一个突破口。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 不可否认的是，2011年对智能电视而言将是具有分水岭性质的一年。智能电视的推出也顺应了三网融合政策，相信，在不久的将来，智能电视能很进入普通家庭，开启一个“智能电视时代”。 </p>', '2012-10-20 14:40:55', 0, '慧聪广电网', 'http://info.broadcast.hc360.com/2011/10/100850466719.shtml', '1001', '自智能电视入市以来，互联网电视的市场份额开始急剧下滑，很多朋友甚至认为，互联网电视只是智能电视的过渡产品，终究会被智能电视所取代。', '未来几年将开启智能电视时代', '2012-10-20 15:40:55', '75', 0);
INSERT INTO t_dp_news
   (`C_ID`, `C_CREATE_USER`, `C_NEWS_CONTENT`, `C_NEWS_CREATE_TIME`, `C_NEWS_ORDER_ID`, `C_NEWS_SOURCE`, `C_NEWS_SOURCE_URL`, `C_NEWS_STSTUS`, `C_NEWS_SUMMARY`, `C_NEWS_TITLE`, `C_UPDATE_TIME`, `C_TYPE_ID`, `C_CLICK_TIME`)
VALUES
   ('110', 'admin', '<p>&nbsp;&nbsp;&nbsp; 记者从有关部门了解到，自从宁波市有线电视网络全面数字化整转以来，全市将有10000户左右的低保家庭享受数字电视优惠政策，获得免费安装的数字电视主终端机顶盒一台，并在收视维护费方面享受优惠价格。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 日前，慈溪数字电视匡堰服务中心的工作人员来到我市第一高山村——岗墩村低保户徐大爷家，为他安装了数字电视。由于徐大爷已经88岁高龄，对数字电视操作不是很懂，工作人员细心地给徐大爷进行了讲解。学会了操作后，徐大爷迫不及待地将频道转到戏曲频道，徐大爷看了电视节目后感激地说：“政府免费替我接上数字电视，这下天天有戏曲节目看了。”</p>\r\n<p>&nbsp;&nbsp;&nbsp; 根据相关规定，我市10000户左右的低保家庭购置数字电视主终端的300元费用由慈溪电视台与市财政共同负担。此外，慈溪数字电视在收视维护费方面也为低保家庭提供优惠，目前，每年一个终端168元的收视费是全免的。</p>\r\n<p>&nbsp;&nbsp;&nbsp; 据了解，慈溪数字电视将在近段时间根据市民政局提供的低保家庭名单，分别联系上门安装事宜，如数字电视线路条件允许即可马上安装，线路条件不具备的要相对延后安装。同时，低保家庭需向广电工作人员出示身</p>', '2012-10-20 14:40:55', 0, '中国宁波网', 'http://info.broadcast.hc360.com/2011/10/090846466159.shtml', '1001', '浙江宁波万户低保户数字电视享优惠', '浙江宁波万户低保户数字电视享优惠', '2012-10-20 15:40:55', '75', 0);
